import { Component } from 'react';
import logo from '../logo.svg';
import './App.css';
import Form from './Form';
import Result from './Result'


class App extends Component {

  state = {
    value: '',
    date: '',
    city: '',
    sunrise: '',
    sunset: '',
    temp: '',
    pressure: '',
    wind: '',
    err: '',
  }

  handleInputChange=(e)=>{
    this.setState({
      value: e.target.value
    })
  }

  handleCitySubmit = e => {
    e.preventDefault()
    console.log("hej");
    const API =`http://api.openweathermap.org/data/2.5/weather?q=${this.state.value}&appid=ed2372e53b834fca6fa2ece9f7c954bc`;

    fetch(API)
      .then(response=> {
        if (response.ok){
          return response;
        }
        throw Error("Nie udalo sie")
      })
      .then(response=> response.json())
      .then(data=>console.log(data))
      .catch(err=>console.log(err))
  }

  render(){
    return (
      <div className="App">
      <Form 
        value={this.state.value} 
        change={this.handleInputChange}
        submit={this.handleCitySubmit}
      />
      <Result/>
      </div>
    );
  }
}

export default App;
